const testWrapper=document.querySelector(".test-wrapper");const testArea=document.querySelector("#test-area");const originText=document.querySelector("#origin-text p").innerHTML;const resetButton=document.querySelector("#reset");const theTimer=document.querySelector(".timer");let timer=[0,0,0,0];let interval;let timeRunning=!1;function runTimer(){let currTime=`${timer[0]< 10?"0"+timer[0]:timer[0]}:${timer[1]< 10?"0"+timer[1]:timer[1]}:${timer[2]< 10?"0"+timer[2]:timer[2]}`;theTimer.innerHTML=currTime;timer[3]++;timer[0]=Math.floor(((timer[3]/100)/60));console.log(timer[0]);timer[1]=Math.floor((timer[3]/100)-(timer[0]*60));console.log(timer[1]);timer[2]=Math.floor((timer[3])-(timer[1]*100)-(timer[0]*6000));console.log(timer[2])}
function spellCheck(e){let textEntered=testArea.value;let originMatch=originText.substring(0,textEntered.length);if(textEntered==originText){clearInterval(interval);testWrapper.style.borderColor="green"}else{if(textEntered==originMatch){testWrapper.style.borderColor="blue"}else{testWrapper.style.borderColor="orange"}}}
function startTimer(e){let testArealength=testArea.value.length;if(testArealength===0&&!timeRunning){timeRunning=!0;interval=setInterval(runTimer,10)}}
function reset(){clearInterval(interval);interval=null;testArea.value="";timer=timer;timeRunning=!1;theTimer.innerHTML="00:00:00";testWrapper.style.borderColor="grey"}
testArea.addEventListener("keypress",startTimer,!1);testArea.addEventListener("keyup",spellCheck,!1);resetButton.addEventListener("click",reset,!1);